@pancakeswap_bp.route('/status', methods=['GET'])
def get_status():
    """Check BSC connection status"""
    try:
        is_connected = web3.is_connected()
        return jsonify({
            'status': 'success',
            'connected': is_connected,
            'network': 'BSC'
        })
    except Exception as e:
        return jsonify({
            'status': 'error',
            'message': str(e)
        }), 500

@pancakeswap_bp.route('/balance', methods=['GET'])
def get_balance():
    """Get wallet balance"""
    try:
        from eth_account import Account
        account = Account.from_key(config.private)
        sender_address = account.address
        
        balance = web3.eth.get_balance(sender_address)
        balance_bnb = web3.from_wei(balance, 'ether')
        
        return jsonify({
            'status': 'success',
            'address': sender_address,
            'balance_wei': balance,
            'balance_bnb': float(balance_bnb)
        })
    except Exception as e:
        return jsonify({
            'status': 'error',
            'message': str(e)
        }), 500

@pancakeswap_bp.route('/swap', methods=['POST'])
def swap_tokens():
    """Execute token swap"""
    try:
        data = request.get_json()
        token_address = data.get('token_address')
        amount_bnb = data.get('amount_bnb', 0.001)
        gas_price_gwei = data.get('gas_price_gwei', 5)
        
        if not token_address:
            return jsonify({
                'status': 'error',
                'message': 'Token address is required'
            }), 400
        
        from eth_account import Account
        account = Account.from_key(config.private)
        sender_address = account.address
        
        # Validate token address
        token_to_buy = web3.to_checksum_address(token_address)
        spend = web3.to_checksum_address("0xbb4cdb9cbd36b01bd1cbaebf2de08d9173bc095c")  # WBNB
        
        # Setup contract
        contract = web3.eth.contract(address=panRouterContractAddress, abi=panabi)
        
        # Get nonce
        nonce = web3.eth.get_transaction_count(sender_address)
        
        # Build transaction
        pancakeswap_txn = contract.functions.swapExactETHForTokens(
            0,  # amountOutMin - set to 0 for simplicity (not recommended for production)
            [spend, token_to_buy],
            sender_address,
            (int(time.time()) + 10000)
        ).build_transaction({
            'from': sender_address,
            'value': web3.to_wei(amount_bnb, 'ether'),
            'gas': 250000,
            'gasPrice': web3.to_wei(gas_price_gwei, 'gwei'),
            'nonce': nonce,
        })
        
        # Sign and send transaction
        signed_txn = web3.eth.account.sign_transaction(pancakeswap_txn, private_key=config.private)
        tx_hash = web3.eth.send_raw_transaction(signed_txn.raw_transaction)
        
        return jsonify({
            'status': 'success',
            'transaction_hash': web3.to_hex(tx_hash),
            'amount_bnb': amount_bnb,
            'token_address': token_address
        })
        
    except Exception as e:
        return jsonify({
            'status': 'error',
            'message': str(e)
        }), 500

